let doc = document;
let container = doc.querySelector('.blog-container')
  
function addElement( parent, child, text){
    let element = doc.createElement(child);
    parent.appendChild(element);
    if(text!=null){
        element.appendChild( doc.createTextNode(text));
    }  
    return element;
  }
  
