function showPage(unit){
    let active = document.querySelector('.pagination1 li.active');
    if(active){
        active.classList.remove('active');
    }  
    
    unit.classList.add('active');
    let pageNum = +unit.innerHTML;
    let start =  (pageNum - 1)* notesOnPage;
    let end = start + notesOnPage;
    let posts = data.slice(start, end);    
    container.innerHTML = '';    
   
    let adminRoot = doc.querySelector('.adminRoot')
    for(let post of posts){
        let close = addElement(container,'div')
        let closeImg = addElement(close,'img')
        closeImg.src= '/images/close.png';
        closeImg.classList.add('close')
        let divMain = addElement(container,'div');
        divMain.classList.add('postItem');
        divMain.date = post.date;
        let divImg = addElement(divMain,'div');
        divImg.classList.add('image');
        let img = addElement(divImg,'img');
        img.src = post.image;
        let divText = addElement(divMain,'div');
        divText.classList.add('textBlog');
        let divDate = addElement(divText,'div');
        divDate.classList.add('date');
        let spanDay = addElement(divDate,'span',post.date);
        spanDay.classList.add('day');
        let spanMonth = addElement(divDate,'span',post.month);
        spanMonth.classList.add('month');
        addElement(divText,'h2',post.header);
        addElement(divText,'p',post.content);
        let moreButton = addElement(divText,'span','читать далее');
        moreButton.classList.add('more');
        let moreContent = addElement(divText,'div',post.moreContent);
        moreContent.classList.add('moreContent');
        addElement(moreContent,'p',post.par1);
        addElement(moreContent,'p',post.par2);
        addElement(moreContent,'p',post.par3);
        addElement(moreContent,'p',post.par4);
        addElement(moreContent,'p',post.par5);
        
        let commentPost = addElement(divText,'div');
        commentPost.classList.add('commentPost');
        let divComment = addElement(commentPost,'div');
        divComment.classList.add('commentsIcon');
        let linkComment = addElement(divComment,'a');
        linkComment.href = '#comments';
        let imgComment = addElement(linkComment,'img');
        imgComment.classList.add('writeComment');
        imgComment.src = '/images/icon-comment.png';
        moreContent.style.display = 'none';
        moreButton.onclick = function(){
          moreContent.style.display = 'block';
          moreButton.style.display = 'none';
        }
        close.onclick = function(){
            divMain.style.display = 'none';
            close.style.display = 'none';            
        } 
    }    
}

